/**
 * Removes 0x from address if present
 * @param {string} address
 * @returns {string}
 */
export function sansPrefix(address: string): string;
/**
 * Adds 0x to address if not already present
 * @param {string} address
 * @returns {string}
 */
export function withPrefix(address: string): string;
/**
 * Adds 0x to address if not already present
 * @param {string} address
 * @returns {string}
 */
export function display(address: string): string;
